import React from "react";

class Forms extends React.Component {
  constructor(){
    super()
    this.handleChange = this.handleChange.bind(this);
    this.state = {
      godInput: '',
      quemSou: 'Não Sei',
    }
  }

  handleChange(event) {
    this.setState ({
      godInput: event.target.value,
      quemSou: event.target.value,
    });
  }


  render() {
    return (
      <div className='form'>
      <h1>Meu formulário</h1>
      <label>
        Quem é você?
        <select name='quemSouEu'>
          <option value= 'Nao Sei'onChange={this.handleChange}>Não Sei</option>
          <option value='Sei lá'>Sei lá</option>
        </select>
      </label>
      <label>Descreva a existência
        <input type='text' name='exists'/>
      </label>
      <label>
        Quem é Deus?
      </label>
        <textarea name='god' rows='10' cols='33' value={this.state.godInput} onChange={this.handleChange} placeholder="RESPONDE AGORA!">
        </textarea>
      </div>
    )
  }
}

export default Forms;
